mapper.select:
SELECT  (@i:=@i+1) AS rank,a.* FROM (
SELECT d.sku_id,h.wh_id,h.owner_id,SUM(d.shipped_qty) AS sku_qty FROM ob_order_detail d,ob_order_header h
WHERE h.wh_id=#{whId}
AND d.`order_header_id`=h.`id`
AND h.stage=#{stage}

 AND h.created_time >#{createTimeFrom}
 AND h.created_time<=#{createTimeTo}
GROUP BY d.sku_id ORDER BY sku_qty DESC
) a,(SELECT @i:=0) AS i

SELECT SUM(d.shipped_qty) AS sku_qty FROM ob_order_detail d,ob_order_header h
WHERE h.wh_id=#{whId}
AND d.`order_header_id`=h.`id`
AND h.stage=#{stage}

 AND h.created_time >#{createTimeFrom}
 AND h.created_time<=#{createTimeTo}

SELECT (@i:=@i+1) AS rank ,t.* FROM(
SELECT d.sku_id,h.wh_id,h.owner_id, COUNT(1) AS sku_qty FROM ob_order_detail
d,ob_order_header h
WHERE h.wh_id=#{whId}
AND d.`order_header_id`=h.`id`
AND h.stage=#{stage}

 AND h.created_time > #{createTimeFrom}
 AND h.created_time <= #{createTimeTo}
GROUP BY d.sku_id) t ,(SELECT @i:=0) AS i  ORDER BY t.sku_qty DESC;

SELECT SUM(sku_qty)  AS total FROM(
SELECT  COUNT(1) AS sku_qty FROM ob_order_detail d,ob_order_header h
WHERE h.wh_id=#{whId}
AND d.`order_header_id`=h.`id`
AND h.stage=#{stage}

 AND h.created_time > #{createTimeFrom}
 AND h.created_time <= #{createTimeTo}
GROUP BY d.sku_id) t
